mapper.update:
UPDATE REPLY
SET RESTEP = RESTEP +1
WHERE BNUM = #{bnum}
AND RESTEP >= #{restep}

UPDATE REPLY
SET CONTENT = #{content}
WHERE RNUM = #{rnum}

UPDATE REPLY
SET LIKECNT = LIKECNT +1
WHERE RNUM = #{rnum}

UPDATE REPLY
SET LIKECNT = LIKECNT -1
WHERE RNUM = #{rnum}

UPDATE REPLY
SET DISLIKECNT = DISLIKECNT +1
WHERE RNUM = #{rnum}

UPDATE REPLY
SET DISLIKECNT = DISLIKECNT -1
WHERE RNUM = #{rnum}
