mapper.update:
update board
set readcnt = readcnt +1
where bnum = #{bnum}

update board
set likecnt = likecnt +1
where bnum = #{bnum}

update board
set likecnt = likecnt -1
where bnum = #{bnum}

update board
set dislikecnt = dislikecnt +1
where bnum = #{bnum}

update board
set dislikecnt = dislikecnt -1
where bnum = #{bnum}

update board
set removeyn = 'y'
where bnum = #{bnum}
